import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { HashLink } from 'react-router-hash-link';
import "../styles/Header.css";

const Header = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [isSticky, setIsSticky] = useState(false);

  // Alternar menú en móviles
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // Alternar dropdowns
  const toggleDropdown = (menu) => {
    setActiveDropdown(activeDropdown === menu ? null : menu);
  };

  // Manejar el scroll para hacer el header sticky
  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.scrollY > 50);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header className={`main-header ${isSticky ? "sticky" : ""}`}>
      <nav className="navbar">
        <div className="logo">
          <Link to="/">
            <img src="/images/logo.png" alt="Logo Eneacoaching" className="logo-img" />
          </Link>
        </div>

        <button className="menu-toggle" onClick={toggleMenu} aria-label="Abrir menú">
          ☰
        </button>

        <ul className={`nav-links ${menuOpen ? "active" : ""}`}>
          <li className={`dropdown ${activeDropdown === "eneagrama" ? "active" : ""}`}>
            <HashLink to="/eneagrama" onClick={() => toggleDropdown("eneagrama")}>Eneagrama ▾</HashLink>
            <ul className="dropdown-menu">
              <li><HashLink smooth to="/eneagrama#que-es">¿Qué es?</HashLink></li>
              <li><HashLink smooth to="/eneagrama#para-que-sirve">¿Para qué sirve?</HashLink></li>
              <li><HashLink smooth to="/eneagrama#tipos">Los 9 tipos de personalidad</HashLink></li>
            </ul>
          </li>
          <Link to="/vicente">Sobre mí</Link>
          <Link to="/contacto">Contacto</Link>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
